<script>
	import { page } from '$app/stores';

	import { copyToClipboard } from '$lib';
	export let url = $page.url.toString();
	export let title = 'Investment Jambi Hub';

	function shareToFacebook() {
		// Ganti URL dan pesan sesuai dengan yang Anda inginkan
		const shareUrl = 'https://www.facebook.com/sharer/sharer.php?u=' + url;
		window.open(shareUrl, '_blank');
	}

	// Fungsi untuk berbagi ke WhatsApp
	function shareToWhatsapp() {
		// Ganti URL dan pesan sesuai dengan yang Anda inginkan
		const shareUrl = 'https://api.whatsapp.com/send?text=' + url;
		window.open(shareUrl, '_blank');
	}

	// Fungsi untuk berbagi ke Twitter
	function shareToTwitter() {
		// Ganti URL dan pesan sesuai dengan yang Anda inginkan
		const shareUrl = `https://twitter.com/intent/tweet?url=${url}&text=${title}`;
		window.open(shareUrl, '_blank');
	}
</script>

